import type { OpenApiSpec } from './openapi-types';

export const openApiSpecs: Record<string, OpenApiSpec> = {
  'cdr-clinical': {
    openapi: '3.0.0',
    info: {
      title: 'CDR Clinical API',
      version: '1.0.0',
      description: 'Healthcare data exchange platform for clinical information sharing',
    },
    paths: {
      '/api/v1/patients': {
        get: {
          operationId: 'getPatients',
          summary: 'Retrieve patient information',
          description: 'Retrieve patient information and basic demographics',
          parameters: [
            {
              name: 'limit',
              in: 'query',
              required: false,
              schema: { type: 'integer', default: 100 },
              description: 'Maximum number of patients to return',
            },
            {
              name: 'offset',
              in: 'query',
              required: false,
              schema: { type: 'integer', default: 0 },
              description: 'Number of patients to skip',
            },
          ],
        },
        post: {
          operationId: 'createPatient',
          summary: 'Create new patient record',
          description: 'Create new patient records with validation',
          requestBody: {
            required: true,
            content: {
              'application/json': {
                schema: {
                  type: 'object',
                  required: ['patientId', 'firstName', 'lastName', 'dateOfBirth'],
                  properties: {
                    patientId: {
                      type: 'string',
                      description: 'Unique patient identifier',
                      example: 'P001',
                    },
                    firstName: {
                      type: 'string',
                      description: 'Patient first name',
                      example: 'John',
                    },
                    lastName: {
                      type: 'string',
                      description: 'Patient last name',
                      example: 'Doe',
                    },
                    dateOfBirth: {
                      type: 'string',
                      format: 'date',
                      description: 'Date of birth (YYYY-MM-DD)',
                      example: '1990-01-15',
                    },
                    gender: {
                      type: 'string',
                      enum: ['male', 'female', 'other', 'unknown'],
                      description: 'Patient gender',
                      example: 'male',
                    },
                    email: {
                      type: 'string',
                      format: 'email',
                      description: 'Patient email address',
                      example: 'john.doe@email.com',
                    },
                    phone: {
                      type: 'string',
                      description: 'Patient phone number',
                      example: '+1-555-123-4567',
                    },
                    address: {
                      type: 'object',
                      properties: {
                        street: { type: 'string', example: '123 Main St' },
                        city: { type: 'string', example: 'Boston' },
                        state: { type: 'string', example: 'MA' },
                        zipCode: { type: 'string', example: '02101' },
                      },
                    },
                    insuranceId: {
                      type: 'string',
                      description: 'Insurance policy ID',
                      example: 'INS-12345',
                    },
                  },
                },
              },
            },
          },
        },
      },
      '/api/v1/patients/{id}/records': {
        get: {
          operationId: 'getPatientRecords',
          summary: 'Fetch clinical history',
          description: 'Fetch complete clinical history and medical records',
          parameters: [
            {
              name: 'id',
              in: 'path',
              required: true,
              schema: { type: 'string' },
              description: 'Patient ID',
              example: 'P001',
            },
          ],
        },
        put: {
          operationId: 'updatePatientRecords',
          summary: 'Update clinical records',
          description: 'Update existing clinical records and notes',
          parameters: [
            {
              name: 'id',
              in: 'path',
              required: true,
              schema: { type: 'string' },
              description: 'Patient ID',
            },
          ],
          requestBody: {
            required: true,
            content: {
              'application/json': {
                schema: {
                  type: 'object',
                  required: ['patientId', 'recordType'],
                  properties: {
                    patientId: {
                      type: 'string',
                      description: 'Patient ID to update',
                      example: 'P001',
                    },
                    recordType: {
                      type: 'string',
                      enum: ['diagnosis', 'prescription', 'lab_result', 'procedure', 'note'],
                      description: 'Type of clinical record',
                      example: 'diagnosis',
                    },
                    recordDate: {
                      type: 'string',
                      format: 'date',
                      description: 'Date of the record',
                      example: '2024-01-15',
                    },
                    providerId: {
                      type: 'string',
                      description: 'Healthcare provider ID',
                      example: 'DR-456',
                    },
                    notes: {
                      type: 'string',
                      description: 'Clinical notes',
                      example: 'Patient presents with...',
                    },
                    icdCode: {
                      type: 'string',
                      description: 'ICD-10 diagnosis code',
                      example: 'J06.9',
                    },
                  },
                },
              },
            },
          },
        },
      },
      '/api/v1/appointments': {
        get: {
          operationId: 'getAppointments',
          summary: 'List appointments',
          description: 'List upcoming and past appointments',
          parameters: [
            {
              name: 'patientId',
              in: 'query',
              required: false,
              schema: { type: 'string' },
              description: 'Filter by patient ID',
            },
            {
              name: 'startDate',
              in: 'query',
              required: false,
              schema: { type: 'string', format: 'date' },
              description: 'Start date filter',
            },
            {
              name: 'endDate',
              in: 'query',
              required: false,
              schema: { type: 'string', format: 'date' },
              description: 'End date filter',
            },
          ],
        },
        post: {
          operationId: 'createAppointment',
          summary: 'Schedule appointment',
          description: 'Schedule new patient appointments',
          requestBody: {
            required: true,
            content: {
              'application/json': {
                schema: {
                  type: 'object',
                  required: ['patientId', 'providerId', 'appointmentDate', 'appointmentTime'],
                  properties: {
                    patientId: {
                      type: 'string',
                      description: 'Patient ID',
                      example: 'P001',
                    },
                    providerId: {
                      type: 'string',
                      description: 'Healthcare provider ID',
                      example: 'DR-456',
                    },
                    appointmentDate: {
                      type: 'string',
                      format: 'date',
                      description: 'Appointment date',
                      example: '2024-02-15',
                    },
                    appointmentTime: {
                      type: 'string',
                      description: 'Appointment time (HH:MM)',
                      example: '14:30',
                    },
                    appointmentType: {
                      type: 'string',
                      enum: ['checkup', 'follow_up', 'consultation', 'procedure', 'emergency'],
                      description: 'Type of appointment',
                      example: 'checkup',
                    },
                    duration: {
                      type: 'integer',
                      description: 'Duration in minutes',
                      example: 30,
                    },
                    notes: {
                      type: 'string',
