                          </Label>
                          <span className="text-2xl font-bold text-primary">{loadTestConfig.rampUpTime}s</span>
                        </div>
                        <Slider
                          value={[loadTestConfig.rampUpTime]}
                          onValueChange={([v]) => setLoadTestConfig(prev => ({ ...prev, rampUpTime: v }))}
                          min={0}
                          max={300}
                          step={10}
                          data-testid="slider-ramp-up"
